const express=require("express"),axios=require("axios"),app=express();app.use("/api",(async(req,res)=>{const apiUrl=req.originalUrl.replace("/api","");try{const response=await axios.get("https://queue-times.com"+apiUrl);res.header("Access-Control-Allow-Origin","*"),res.json(response.data)}catch(error){res.status(error.response?error.response.status:500).send(error.message)}}));const PORT=process.env.PORT||4e3;app.listen(PORT,(()=>console.log(`Proxy running on port ${PORT}`)));